mapper.insert:
insert into mid.tm_com_auth(auth_nm
                   	  , act_yn
               	      , crt_id
               	      , crt_dt
               	      , mdfy_id
               	      , mdfy_dt)
		        values(#{auth_nm}
		              ,#{act_yn}
		              ,#{crt_id}
		              ,to_char(now(), 'YYYYMMDDHH24MISS'::text)
		              ,#{mdfy_id}
		              ,to_char(now(), 'YYYYMMDDHH24MISS'::text))

mapper.update:
update mid.tm_com_auth
   set auth_nm = #{auth_nm}
     , act_yn = #{act_yn}
     , crt_id = #{crt_id}
     , crt_dt = to_char(now(), 'YYYYMMDDHH24MISS'::text) 
     , mdfy_id = #{mdfy_id}
     , mdfy_dt = to_char(now(), 'YYYYMMDDHH24MISS'::text)
where auth_id = #{auth_id}

mapper.delete:
delete from mid.tm_com_user_auth
	  where user_id=#{user_id}
	    and auth_id=#{auth_id}
