mapper.update:

update tb_lead
set lead_name=#{lead_name}, cust_no =#{cust_no}, emp_no =#{emp_no},
contact_day= date_format(#{contact_day}, '%Y%m%d'), rank_cd = #{rank_cd}, reason_cd = #{reason_cd},
remark_cn = #{remark_cn}, update_date = now(), possibility_cd = #{possibility_cd}, lead_status_cd = #{lead_status_cd}
where lead_no = #{lead_no}


update tb_lead
set del_yn="Y"
where lead_no = #{lead_no}

mapper.select:
SELECT P.* FROM ( SELECT cust_no, cust_name FROM tb_cust WHERE del_yn = 'N'
AND (cust_name like concat('%', #{s_cust_name}, '%'))
) P LIMIT #{page.limitStartNum}, #{page.pageSize}

SELECT P.* FROM ( SELECT user_no, user_nm FROM tb_user WHERE act_yn = 'Y' AND user_id != 'admin'
AND (user_nm like concat('%', #{s_emp_name}, '%'))
) P LIMIT #{page.limitStartNum}, #{page.pageSize}

mapper.insert:
INSERT INTO tb_interest_item
	(lead_no, main_cate_cd, mid_cate_cd, small_cate_cd, qty, list_price)
VALUES
	(
		#{lead_no}, #{main_cate_cd}, #{mid_cate_cd}, #{small_cate_cd}, #{qty}, #{list_price}
	)
