mapper.select:
SELECT P.* FROM ( SELECT cust_no, cust_name FROM tb_cust WHERE del_yn = 'N'
AND (cust_name like concat('%', #{s_cust_name}, '%'))
) P LIMIT #{page.limitStartNum}, #{page.pageSize}

SELECT P.* FROM ( SELECT user_no, user_nm FROM tb_user WHERE act_yn = 'Y' AND user_id != 'admin'
AND (user_nm like concat('%', #{s_emp_name}, '%'))
) P LIMIT #{page.limitStartNum}, #{page.pageSize}

SELECT P.* FROM ( SELECT code AS main_cate_cd, code_name AS main_cate_name FROM tb_code WHERE code_no='1015'
AND (code_name like concat('%', #{s_main_cate_name}, '%'))
) P LIMIT #{page.limitStartNum}, #{page.pageSize}

SELECT P.* FROM ( SELECT DISTINCT a.code AS mid_cate_cd, a.code_name AS mid_cate_name FROM tb_code a, tb_price_book b WHERE a.code=b.mid_cate_cd AND a.code_no='1016' AND b.main_cate_cd=#{main_cate_cd}
AND (a.code_name like concat('%', #{s_mid_cate_name}, '%'))
) P LIMIT #{page.limitStartNum}, #{page.pageSize}

SELECT P.* FROM ( SELECT DISTINCT a.code AS small_cate_cd, a.code_name AS small_cate_name, b.list_price FROM tb_code a, tb_price_book b WHERE a.code = b.small_cate_cd AND b.main_cate_cd = #{main_cate_cd} AND b.mid_cate_cd = #{mid_cate_cd} AND a.code_no='1017'
AND (a.code_name like concat('%', #{s_small_cate_name}, '%'))
) P LIMIT #{page.limitStartNum}, #{page.pageSize}
