mapper.update:
update tb_task
set 
    subject   = #{subject}
  , cust_no   = #{cust_no}
  , emp_no    = #{emp_no}
  ,   next_day  = DATE_FORMAT(#{next_day}, '%Y%m%d')
  , dtype_cd  = #{dtype_cd}
  , lead_no     = #{lead_no}
  , oppty_no  = #{oppty_no}
  , score_cd  = #{score_cd}
  , location  = #{location}
  , remark_cn = #{remark_cn}
where task_no = #{task_no}

update tb_task
set del_yn = 'Y'
where task_no = #{task_no}

mapper.select:
SELECT P.* FROM ( SELECT cust_no, cust_name FROM tb_cust WHERE del_yn = 'N'
AND (cust_name like concat('%', #{s_cust_name}, '%'))
)p LIMIT #{page.limitStartNum}, #{page.pageSize}

SELECT P.* FROM ( SELECT emp_no, emp_name FROM tb_emp WHERE del_yn = 'N'
AND (emp_name like concat('%', #{s_emp_name}, '%'))
)p LIMIT #{page.limitStartNum}, #{page.pageSize}

SELECT P.* FROM ( SELECT lead_no, lead_name FROM tb_lead WHERE del_yn = 'N'
AND (lead_name like concat('%', #{s_lead_name}, '%'))
)p LIMIT #{page.limitStartNum}, #{page.pageSize}

SELECT P.* FROM ( SELECT oppty_no, oppty_name FROM tb_oppty WHERE del_yn = 'N'
AND (oppty_name like concat('%', #{s_oppty_name}, '%'))
)p LIMIT #{page.limitStartNum}, #{page.pageSize}
